function formatTemp(high, low){
  var temp
    if (units == "imperial") {
        if (low){
          temp = Math.round(parseFloat(high)) + '/' + Math.round(parseFloat(low))
        }
        else {
          temp = Math.round(parseFloat(high)) + '째F'
        }
    }
    else { // metric
        if (low){
          temp = Math.round(parseFloat(high)) + '/' + Math.round(parseFloat(low))
        }
        else {
          temp = Math.round(parseFloat(high)) + '째C'
        }
    }
    return temp;
}

